mapper.update:
UPDATE tbl_product_review
SET
	review_content = #{review_content},
	review_rate = #{review_rate}
WHERE product_review_id = #{product_review_id}

mapper.select:
SELECT *
	FROM (

	    WHERE product_id = #{product_id}
	    ORDER BY review_rate DESC, review_date DESC
	)
	WHERE rownum = 1




		AND r.writer_id = #{data}


		AND r.product_review_id = #{data}


		AND r.product_id = #{data}


ORDER BY review_rate DESC, review_date DESC
