mapper.select:

SELECT * FROM tbl_myplant_schedule
WHERE myplant_id = #{myplant_id}
ORDER BY myplant_schedule_date

mapper.update:
UPDATE tbl_myplant_schedule
SET
    myplant_schedule_date = #{myplant_schedule_date},
    myplant_schedule_do   = #{myplant_schedule_do}
WHERE schedule_id = #{schedule_id}
