mapper.select:
SELECT user_id, name
FROM TBL_USER
WHERE user_id = #{user_id}

SELECT username 
FROM TBL_USER 
WHERE email = #{email}

SELECT username 
FROM TBL_USER 
WHERE phone = #{phone}

SELECT COUNT(*)
FROM TBL_USER
WHERE username = #{username}
AND
email = #{email}

SELECT COUNT(*)
FROM TBL_USER
WHERE username = #{username}
AND
phone = #{phone}

mapper.update:
		UPDATE TBL_USER
    	SET password = #{password}
    	WHERE username = #{username}

mapper.select:
SELECT COUNT(*)
FROM TBL_USER
WHERE username = #{username}

SELECT COUNT(*)
FROM TBL_USER
WHERE email = #{email}

SELECT COUNT(*)
FROM TBL_USER
WHERE phone = #{phone}

SELECT username, confirm_service, confirm_userinfo, confirm_event 
FROM TBL_USER
WHERE username = #{username}

SELECT user_id, grade_id, nickname, profile_filename, intro
FROM TBL_USER
WHERE user_id = #{user_id}

SELECT COUNT(*)
FROM TBL_REPLY
WHERE user_id = #{user_id}

SELECT NVL(SUM(view_count),0)
FROM TBL_BOARD
WHERE user_id = #{user_id}
